import React, { useContext } from 'react';
import { Link } from 'react-router-dom'; // Import Link from react-router-dom
import { IoMdClose } from 'react-icons/io';
import { CartContext } from '../contexts/CartContext';

const ViewCartPage = () => {
    const { cart, addToCart, removeFromCart } = useContext(CartContext);

    const handleAddToCart = (product) => {
        addToCart(product, product.id);
    };

    return (
        <div className="container mx-auto mt-60"> {/* Adjust the top margin as needed */}
            <h2 className="text-4xl font-semibold mb-4">View Cart</h2>
            <div className="grid grid-cols-1 gap-4">
                {cart.map((item) => (
                    <div key={item.id} className="bg-white shadow-md px-4 py-4 mb-4 max-w-md">
                        <div className="flex items-center justify-between">
                            <div className="w-20">
                                <img src={item.image} alt={item.name} className="h-full w-full object-contain" />
                            </div>
                            <div className="ml-4 flex-grow">
                                <h3 className="text-lg font-semibold">{item.name}</h3>
                                <p className="text-indigo-600 font-semibold">${item.price}</p>
                                {/* Remove button */}
                                <button onClick={() => removeFromCart(item.id)} className="text-red-500 hover:text-red-700"><IoMdClose /></button>
                            </div>
                        </div>
                    </div>
                ))}
            </div>
            {/* Checkout Button */}
            <div className="mt-8">
                <Link to="/checkout" className="bg-blue-500 text-white px-4 py-2 rounded hover:bg-blue-600">Proceed to Checkout</Link>
            </div>
        </div>
    );
};

export default ViewCartPage;
