import React, { useContext, useState } from 'react';
import { Link } from 'react-router-dom';
import { BsPlus, BsEyeFill, BsCheck } from 'react-icons/bs';
import { CartContext } from '../contexts/CartContext';

const Product = ({ product }) => {
  const { addToCart } = useContext(CartContext);
  const [addedToCart, setAddedToCart] = useState(false);

  const { id, image, category, title, price } = product;

  const handleAddToCart = () => {
    addToCart(product); // Assuming addToCart function takes product as an argument
    setAddedToCart(true); // Update state to indicate item has been added to cart
  };

  return (
    <div>
      <div className='border border-[#e4e4e4] h-[300px] mb-4 relative overflow-hidden group transition'>
        <div className='w-full h-full flex justify-center items-center'>
          {/* image */}
          <div className='w-[200px] mx-auto flex justify-center items-center'>
            <img className='max-h-[160px] group-hover:scale-110 transition duration-300' src={image} alt='' />
          </div>
        </div>
         {/* buttons */}
        <div className='absolute top-6 right-11 group-hover:right-5 p-2 flex flex-col items-center justify-center 
        gap-y-2 opacity-0 group-hover:opacity-100 transition-all duration-3000'>
          <button onClick={handleAddToCart}>
            <div className='flex justify-center items-center text-white w-12 h-12 bg-red-500'>
              {addedToCart ? (
                <BsCheck className='text-3xl' /> // Show check mark if added to cart
              ) : (
                <BsPlus className='text-3xl' /> // Show plus icon if not added to cart
              )}
            </div>
          </button>

          <Link to={`/product/${id}`} 
          className='w-12 h-12 bg-white flex justify-center items-center text-primary drop-shadow-xl'>
            <BsEyeFill />
          </Link>
        </div>
      </div>
      {/* category & title & price */}
       <div>
         <div className='text-sm capitalize text-gray-500 mb-1'>{category}</div>
         <Link to={`/product/${id}`}>
         <h2 className='font-semibold mb-1'>{title}</h2>
         </Link>
         <div className='font-semibold'>$ {price}</div>
       </div>
    </div>
  );
};

export default Product;
