import React, { useContext } from 'react';
import { CartContext } from '../contexts/CartContext';

const ViewCartPage = () => {
    const { cart, addToCart } = useContext(CartContext);

    const handleAddToCart = (product) => {
        addToCart(product, product.id);
    };

    return (
        <div className="container mx-auto mt-20"> {/* Adjust the top margin as needed */}
            <h2 className="text-4xl font-semibold mb-4">View Cart</h2>
            <div className="grid grid-cols-1 gap-4">
                {cart.map((item) => (
                    <div key={item.id} className="bg-white shadow-md px-8 py-6 mb-4">
                        <h3 className="text-lg font-semibold">{item.name}</h3>
                        <p className="text-gray-600">{item.description}</p>
                        <p className="text-indigo-600 font-semibold">${item.price}</p>
                        {/* Add more details as needed */}
                    </div>
                ))}
            </div>
        </div>
    );
};

export default ViewCartPage;
