import React, { useState } from 'react';
import { Link } from 'react-router-dom';
import { BsPlus, BsCheck } from 'react-icons/bs';
import { BsEyeFill } from 'react-icons/bs';

const ProductCard = ({ product }) => {
  const { id, image, name, price } = product;
  const [addedToCart, setAddedToCart] = useState(false);

  const addToCart = (product, id) => {
    // Add logic to add the product to the cart
    setAddedToCart(true); // Update state to indicate the item has been added to cart
  };

  return (
    <div className='relative group overflow-hidden'>
      {/* image */}
      <img src={image} alt={name} className='w-full h-[220px] object-cover object-center' />
      {/* overlay */}
      <div className='absolute inset-0 bg-black bg-opacity-40 flex justify-center items-center opacity-0 group-hover:opacity-100 transition-all duration-300'>
        <button onClick={() => addToCart(product, id)} className='w-full h-full flex justify-center items-center text-white bg-primary bg-opacity-90 hover:bg-opacity-100 focus:outline-none'>
          {addedToCart ? (
            <BsCheck className='text-3xl' /> // Show check mark if added to cart
          ) : (
            <BsPlus className='text-3xl' /> // Show plus icon if not added to cart
          )}
        </button>
      </div>
      {/* buttons */}
      <div className='absolute top-6 right-11 group-hover:right-5 p-2 flex flex-col items-center justify-center gap-y-2 opacity-0 group-hover:opacity-100 transition-all duration-300'>
        <button onClick={() => addToCart(product, id)}>
          <div className='flex justify-center items-center text-white w-12 h-12 bg-red-500'>
            <BsPlus className='text-3xl' />
          </div>
        </button>
        <Link to={`/product/${id}`} className='w-12 h-12 bg-white flex justify-center items-center text-primary drop-shadow-xl'>
          <BsEyeFill />
        </Link>
      </div>
    </div>
  );
};

export default ProductCard;
