import React from 'react';
import { Link } from 'react-router-dom';
import { IoMdAdd, IoMdClose, IoMdRemove } from 'react-icons/io';

const CartItem = ({ item }) => {
  const { id, title, image, price, amount } = item;

  return ( 
    <div className='flex gap-x-4 py-2 lg:px-6 border-b border-gray-200 w-full font-light text-gray-500'>
      <div className='flex items-center'> 
        {/* Image */}
        <Link to={`/product/${id}`}>
          <img className='max-w-[80px]' src={image} alt={title} /> 
        </Link>

        <div className='flex flex-col ml-4'>
          {/* Title & Remove Icon */}
          <div className='flex justify-between mb-2'>
            {/* Title */}
            <Link to={`/product/${id}`} className='text-sm uppercase font-medium max-w-[240px] text-primary hover:underline'>
              {title}
            </Link>
            {/* Remove Icon */}
            <div className='text-xl cursor-pointer'>
              <IoMdClose className='text-gray-500 hover:text-red-500 transition' />
            </div>
          </div>

          {/* Quantity */}
          <div className='flex flex-1 max-w-[100px] items-center h-full border text-primary font-medium'>
            {/* Minus Icon */}
            <div className='flex flex-1 justify-center items-center cursor-pointer'>
              <IoMdRemove />
            </div>
            {/* Amount */}
            <div className='h'>{amount}</div>
            {/* Plus Icon */}
            <div className='flex-1 justify-center items-center cursor-pointer'>
              <IoMdAdd />
            </div>
          </div>

          {/* Item Price */}
          <div className='flex items-center justify-around'>
            <div className='text-primary font-medium'>$ {price}</div>
            {/* Final Price */}
            <div className='text-primary font-medium'>
              {`$ ${parseFloat(price * amount).toFixed(2)}`}
            </div>
          </div>
        </div>
      </div>
    </div>
  );
};

export default CartItem;
