import React, { useState, useEffect } from 'react';
import Product from './Product';

const ProductPage = () => {
  const [products, setProducts] = useState([]);
  const [sortingCriteria, setSortingCriteria] = useState('default');

  useEffect(() => {
    // Fetch products from the API
    fetch('https://fakestoreapi.com/products')
      .then(response => response.json())
      .then(data => setProducts(data))
      .catch(error => console.error('Error fetching products:', error));
  }, []);

  const handleSortingChange = (e) => {
    setSortingCriteria(e.target.value);
  };

  const sortProducts = (products) => {
    switch (sortingCriteria) {
      case 'price-low-to-high':
        return [...products].sort((a, b) => a.price - b.price);
      case 'price-high-to-low':
        return [...products].sort((a, b) => b.price - a.price);
      case 'default':
      default:
        return products;
    }
  };

  const sortedProducts = sortProducts(products);

  return (
    <div className="flex justify-center items-center h-screen">
      <div className="container mx-auto grid grid-cols-4 gap-0">
        {/* Sorting dropdown */}
        <div className="col-span-5 mb-4">
          <label htmlFor="sorting">Sort by:</label>
          <select id="sorting" value={sortingCriteria} onChange={handleSortingChange}>
            <option value="default">Default</option>
            <option value="price-low-to-high">Price: Low to High</option>
            <option value="price-high-to-low">Price: High to Low</option>
          </select>
        </div>

        {/* Render sorted products */}
        {sortedProducts.map(product => (
          <div key={product.id} className="w-full sm:w-1/2 md:w-1/3 lg:w-1/3 xl:w-1/3">
            <Product product={product} />
          </div>
        ))}
      </div>
    </div>
  );
};

export default ProductPage;
