import React, { createContext, useContext, useState } from 'react';

const AuthContext = createContext();

export const useAuth = () => {
  return useContext(AuthContext);
};

export const AuthProvider = ({ children }) => {
  const [isLoggedIn, setIsLoggedIn] = useState(false); // Change variable name to isLoggedIn

  const login = () => {
    // Perform login logic
    setIsLoggedIn(true); // Use setIsLoggedIn to update isLoggedIn
  };

  const logout = () => {
    // Perform logout logic
    setIsLoggedIn(false); // Use setIsLoggedIn to update isLoggedIn
  };

  return (
    <AuthContext.Provider value={{ isLoggedIn, login, logout }}> {/* Use isLoggedIn here */}
      {children}
    </AuthContext.Provider>
  );
};
