import React from 'react';
import { Link } from 'react-router-dom';
import { IoMdAdd, IoMdClose, IoMdRemove } from 'react-icons/io';

const CartItem = ({ item }) => {
  const { id, title, image, price, amount } = item;

  return ( 
    <div className='w-full min-h-150 flex items-center gap-x-4'> {/* Corrected className */}
      {/* image */}
      <Link to={`/product/${id}`}>
        <img className='max-w-80' src={image} alt='' /> 
      </Link>

      <div className='w-full flex flex-col'>
        {/* title & remove icon */}
        <div className='flex justify-between mb-2'>

          {/* qty */}
          <div className='flex flex-1 max-w-100 items-center h-full border text-primary font-medium'> {/* Corrected className */}
            {/* minus icon */}
            <div className='flex-1 h-full flex justify-center items-center cursor-pointer'>
              <IoMdRemove />
            </div>
            {/* amount */}
            <div className='h-full flex justify-center items-center px-2'>{amount}</div>
            {/* plus icon*/}
            <div className='flex-1 h-full flex justify-center items-center cursor-pointer'>
              <IoMdAdd />
            </div>
          </div>

          {/* item price */}
          <div className='flex-1 flex items-center justify-around'>$ {price}</div>

          {/* final price */}
          {/* make the price at 2 decimals */}
          <div className='flex-1 flex justify-end items-center text-primary font-medium'>{`$ ${parseFloat(price * amount).toFixed(2)}`}</div>
        </div>
      </div>
    </div>
  );
};

export default CartItem;
