import React, { useContext } from 'react';
import { Link } from 'react-router-dom';
import { IoMdClose, IoMdAdd, IoMdRemove } from 'react-icons/io';
import { CartContext } from '../contexts/CartContext';

const ViewCartPage = () => {
    const { cart, addToCart, removeFromCart, increaseAmount, decreaseAmount } = useContext(CartContext);

    const handleAddToCart = (product) => {
        addToCart(product, product.id);
    };

    const handleIncreaseAmount = (id) => {
        increaseAmount(id);
    };

    const handleDecreaseAmount = (id) => {
        decreaseAmount(id);
    };

    // Calculate total price of all items in cart
    const total = cart.reduce((acc, item) => acc + (item.price * item.amount), 0);

    return (
        <div className="container mx-auto mt-60">
            <h2 className="text-4xl font-semibold mb-4">View Cart</h2>
            <div className="grid grid-cols-1 gap-4">
                {cart.map((item) => (
                    <div key={item.id} className="bg-white shadow-md flex items-center justify-between px-2 py-2 mb-4 w-72">
                        <div className="flex items-center">
                            <img src={item.image} alt={item.name} className="w-14 h-14 object-contain mr-4" />
                            <div>
                                <h3 className="text-lg font-semibold">{item.name}</h3>
                                <h3 className="text-sm">{item.title}</h3>
                                <p className="text-blue-600 font-semibold">${item.price}</p>
                            </div>
                        </div>
                        <div className="flex items-center">
                            <button onClick={() => handleDecreaseAmount(item.id)} className="border border-gray-400 rounded-full p-1 hover:bg-gray-200"><IoMdRemove /></button>
                            <span className="mx-2">{item.amount}</span>
                            <button onClick={() => handleIncreaseAmount(item.id)} className="border border-gray-400 rounded-full p-1 hover:bg-gray-200"><IoMdAdd /></button>
                        </div>
                        <button onClick={() => removeFromCart(item.id)} className="text-red-500 hover:text-red-700"><IoMdClose /></button>
                    </div>
                ))}
            </div>
            <div className="text-lg font-semibold mb-4">Total: ${total.toFixed(2)}</div> {/* Display total price */}
            <div className="mt-8">
                <Link to="/checkout" className="bg-blue-500 text-white px-4 py-2 rounded hover:bg-blue-600">Proceed to Checkout</Link>
            </div>
            <div className="mt-16"></div> {/* Space between cart items and footer */}
        </div>
    );
};

export default ViewCartPage;
